hyb_device hyb_inline static bool8 operator<(const float8& l, const float8& r) {
	
	bool8 res;
	res.mask = 0;
	float* pl = (float*)&l;
	float* pr = (float*)&r;
	for(int i = 0; i < 8; ++i) {
		res.mask |= ((pl[i] < pr[i]) << i);
	}

	return res;
}

hyb_device hyb_inline static bool8 operator>(const float8& l, const float8& r) {
	
	bool8 res;
	res.mask = 0;
	float* pl = (float*)&l;
	float* pr = (float*)&r;
	for(int i = 0; i < 8; ++i) {
		res.mask |= ((pl[i] > pr[i]) << i);
	}

	return res;
}

hyb_device hyb_inline static bool8 operator>(const float8& l, const float& r) {
	
	bool8 res;
	res.mask = 0;
	float* pl = (float*)&l;
	#pragma unroll
	for(int i = 0; i < 8; ++i) {
		res.mask |= ((pl[i] > r) << i);
	}

	return res;
}

hyb_device hyb_inline static float8 operator+(const float8& l, const float8&r) {
	float8 res;
	float* pres = (float*)&res;
	float* pl = (float*)&l;
	float* pr = (float*)&r;
	for(int i = 0; i < 8; ++i) {
		pres[i] = pl[i] + pr[i];
	}

	return res;
}

hyb_device hyb_inline static float8 operator+(const float8& l, const float&r) {
	float8 res;
	res.x = l.x + r;
	res.y = l.y + r;
	res.z = l.z + r;
	res.w = l.w + r;
	res.x2 = l.x2 + r;
	res.y2 = l.y2 + r;
	res.z2 = l.z2 + r;
	res.w2 = l.w2 + r;
	return res;
}

namespace hybridizer {
	template<typename Vec, typename Mask>
	hyb_device hyb_inline Vec select(const Mask& m, const Vec& l, const Vec& r);

	template<>
	hyb_device hyb_inline float8 select<float8, bool8>(const bool8& m, const float8& l, const float8& r) {
		float8 res;
		float* pres = (float*)&res;
		float* pl = (float*)&l;
		float* pr = (float*)&r;
		int mask = 1;
		for(int i = 0; i < 8; ++i) {
			if(m.mask & mask) {
				pres[i] = pl[i];
			}
			else {
				pres[i] = pr[i];
			}

			mask <<= 1;
		}
		return res;
	}

