	hyb_device hyb_inline VecOutput shuffleVector(const VecInput& l, const VecInput& r, const Mask& m);

	template<>
	hyb_device hyb_inline int8 shuffleVector(const int8& l, const int8& r, const int8& m) {
		int8 res;
		int* pl = (int*)&l;
		int* pr = (int*)&r;
		int* pm = (int*)&m;
		int* pres = (int*)&res;
		#pragma unroll
		for(int i = 0; i < 8; ++i) {
			int index = pm[i];
			if(index >= 0 && index < 8) 
				pres[i] = pl[index];
			else if(index >= 0) 
				pres[i] = pr[index];
		}
		return res;
	}

